import React, { Component } from 'react';
import '../Styling/Option.css';
import ReactModal from 'react-modal';
import parse from 'html-react-parser';

class Option extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false
    };
  }

  handleOpenModal = () => {
    this.setState({ showModal: true });
  }

  handleCloseModal = () => {
    this.setState({ showModal: false });
  }

  render() {
    return (
      <div id="opt-cont">
        <div id="opt-name" onClick={this.props.onClick}>{this.props.option_name}</div>
        <div id="check"></div>
        <ReactModal
          isOpen={this.state.showModal}
          onRequestClose={this.handleCloseModal}
          contentLabel="Option Description Modal"
          className="option-modal"
          overlayClassName="option-modal-overlay"
        >
          <div id='op-desc'>{parse(this.props.option_description)}</div>
          <button onClick={this.handleCloseModal}>Close</button>
        </ReactModal>
        <button onClick={this.handleOpenModal}>Close</button>
      </div>
    );
  }
}

export default Option;
