import React, { Component } from 'react';
import '../Styling/Option.css';
import parse from 'html-react-parser';

class Option extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showDialog: false
    };
  }

  handleClick = () => {
    this.setState(prevState => ({
      showDialog: !prevState.showDialog
    }));
  };

  render() {
    return (
      <div id="opt-cont">
        <div id="opt-name" onClick={this.handleClick}>
          {this.props.option_name}
        </div>
        <div id="check"></div>
        {this.state.showDialog && (
          <div id="option-dialog">
            <div id="close-button" onClick={this.handleClick}>
              &times;
            </div>
            <div id="op-desc">{parse(this.props.option_description)}</div>
          </div>
        )}
      </div>
    );
  }
}

export default Option;
