import {React, Component} from 'react';
import '../Styling/Option.css';
import Collapsible from 'react-collapsible';

class Option extends Component{
    render(){
        return(
                <div id = "opt-cont">
                        <div id = 'opt-desc'>{this.props.option_name}</div>
                        <Collapsible id = "op-container" trigger = "More-Info">
                            <p>{this.props.option_description}</p>
                        </Collapsible>  
                </div>


        )
    }
}

export default Option;
