import React from 'react';
import '../Styling/Modal.css';
import ReactModal from 'react-modal';

const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      width:'80%',
    },
  };

const Modal = (props) => {
    let subtitle;
    const [modalIsOpen, setIsOpen] = React.useState(false);

    function openModal() {
      setIsOpen(true);
    }

    function afterOpenModal() {
      // references are now sync'd and can be accessed.
      subtitle.style.color = '#f00';
    }

    function closeModal() {
      setIsOpen(false);
    }

  return (
    <div id = "modal-cont">
      <button onClick={openModal}>More Info</button>
      <ReactModal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <button id = "close-btn" onClick={closeModal}>X</button>
        <h2 id = "option-name">Option Name</h2>
        <p id = "option-description">We understand how difficult times can leave people 
                                                not knowing their next steps. We want to provide users
                                                with the information they need in order to take the next 
                                                steps in their lives and provide the resources to be 
                                                successful in their ventures.</p>
      </ReactModal>
    </div>
  );
};

export default Modal;
