import React, { Component, useState } from 'react';
import '../Styling/Option.css';
import Collapsible from 'react-collapsible';
import parse from 'html-react-parser';
import Modal from './Modal'; // Import the modal component

class Option extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false, // Track the visibility of the modal
    };
  }

  toggleModal = () => {
    this.setState((prevState) => ({
      showModal: !prevState.showModal, // Toggle the visibility of the modal
    }));
  };

  render() {
    return (
      <div id="opt-cont">
        <div id="opt-name" onClick={this.props.onClick}>
          {this.props.option_name}
        </div>
        <div id="check"></div>
        <Collapsible
          contentInnerCLassName="content"
          classParentString="op-container"
          trigger="More-Info"
        >
          <div id="op-desc">{parse(this.props.option_description)}</div>
          <button onClick={this.toggleModal}>More Options</button>
          {this.state.showModal && (
            <Modal onClose={this.toggleModal}>
              {/* Place the additional option details here */}
            </Modal>
          )}
        </Collapsible>
      </div>
    );
  }
}

export default Option;
